import React from 'react';
import { TouchableOpacity, Text, SafeAreaView, View, TextInput, } from 'react-native';

function Button(props) {
    return (
        <TouchableOpacity disabled={props.disable} style={{
            height: 50,
            width:'90%',
            borderColor:'#808080', 
            borderWidth: 1,
            borderRadius:25,
            margin: 20,
            alignItems: 'center',
            marginTop: 10,
            marginBottom: 10,
            flexDirection: 'row',
            paddingHorizontal: 15,
            justifyContent:'center',
            backgroundColor:props.disable?'white':'#1C2348',
            shadowOffset:{
                height:2,
                width:2,
            },
            shadowRadius:5,
            shadowColor:'#808080',
            shadowOpacity:.4,
            elevation:5,
        }}>
        <Text style={{
            color:!props.disable?'white':'#1C2348',
            fontWeight:'bold',
        }} >
            Log In
        </Text>
        </TouchableOpacity>
    );
}

export default Button;
