import React from 'react';
import {TouchableOpacity,Text,SafeAreaView,
    Image,ScrollView,View,Dimensions} from 'react-native'
import Input from './../Components/Input'
import { FontAwesome } from '@expo/vector-icons';
import { Entypo } from '@expo/vector-icons';
import icon from '../assets/a.png';
import Button from '../Components/Button';


function LogIn(props) {
    const [UserName,setUserName] = React.useState(null);
    const [Password,setPassword] = React.useState(null);
    const window=Dimensions.get('window')
    return (
        <ScrollView style={{
           
        }}>
        <View style={{
            height:window.height,
            alignItems:'center',
            justifyContent:'center',
        }}>
        <View style={{
            alignItems:'center',
            justifyContent:'center',
            height:100,
            width:100,
            borderRadius:70,
            backgroundColor:'#1C2348',
            marginBottom:40,
            
            
        }}>
           <Image style={{
               height:80,
               width:80,
           }} source={icon}/>
        </View>
        <Input onChange={setUserName} placeholder='User name' icon={()=>(
            <FontAwesome name="user-circle-o" size={30} color="#1C2348" />
        )}/>
        <Input onChange={setPassword} placeholder='Password' icon={()=>(
            <Entypo name="lock" size={30} color="#1C2348" />
        )}/>
        <Button disable={UserName&&Password?false:true}>

        </Button>
        <TouchableOpacity style={{
            marginTop: 20,
        }}>
        <Text style={{
            fontWeight:'bold',
            color:'#1C2348',
        }} >
            Forget Password ?
        </Text>
        </TouchableOpacity>
        <TouchableOpacity style={{
            marginTop: 20,
        }}>
        <Text style={{
            fontWeight:'bold',
            color:'#1C2348',
        }} >
            Create new account
        </Text>
        </TouchableOpacity>
        </View>

        
        
        </ScrollView>
    );
}

export default LogIn;
